import React from "react";
import './index.css';

class Register extends React.Component {
    constructor(props) {
        super();
        this.state = {
            email : '',
            password : '',
            name: ''
        }
    }

    onNameChange = (event) => {
        this.setState({ name: event.target.value })
    }

    onEmailChange = (event) => {
        this.setState({ email: event.target.value })
    }

    onPasswordChange = (event) => {
        this.setState({ password: event.target.value })
    }

    onSubmitSignIn = () => {
        fetch('https://login-app-api-ap19.onrender.com/register', {
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
            email: this.state.email,
            password: this.state.password,
            name: this.state.name
            })
        })
            .then(response => response.json())
            .then(user => {
                if (user.id) {
                    this.props.loadUser(user)
                    this.props.onRouteChange('home');
            }
        })
    }

    render () {
        return  (
            <article className="bg-purple br3 ba b--black-10 mv4 w-100 w-50-m w-25-l mw6 shadow-5 center">
            <main className="pa4 black-80">
                <div className="measure">
                    <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
                    <legend className="f1 white fw6 ph0 mh0">REGISTER</legend>
                    <div className="mt3">
                        <label className="db white fw6 lh-copy f6" htmlFor="name">Name</label>
                        <input 
                            className="shadow-5 pa2 input-reset ba bg-white hover-bg-black hover-white w-100" 
                            type="text" 
                            name="name"  
                            id="name"
                            onChange={this.onNameChange}/>
                    </div>
                    <div className="mt3">
                        <label className="db fw6 white lh-copy f6" htmlFor="email-address">Email</label>
                        <input 
                            className="shadow-5 pa2 input-reset ba bg-white hover-bg-black hover-white w-100" 
                            type="email" 
                            name="email-address"  
                            id="email-address"
                            onChange={this.onEmailChange}/>
                    </div>
                    <div className="mv3">
                        <label className="db fw6 white lh-copy f6" htmlFor="password">Password</label>
                        <input 
                            className="shadow-5 b pa2 input-reset ba bg-white hover-bg-black hover-white w-100" 
                            type="password" 
                            name="password"  
                            id="password"
                            onChange={this.onPasswordChange}/>
                    </div>
                    </fieldset>
                        <div className="">
                            <input 
                                onClick={this.onSubmitSignIn}
                                className="b ph3 pv2 pointer white input-reset ba b--black bg-black grow pointer f6 dib" 
                                type="submit" 
                                value="Register"/>
                        </div>
                        <div className="lh-copy mt3">
                         <p 
                            onClick={() => this.props.onRouteChange('signin')}
                            className="pointer f6 link dim white db">Sign in
                         </p>
                        </div>
                    </div>
                </main>
        </article>
        )
    }
}

export default Register
