import React , {Loader, useState, useEffect} from "react";
import RingLoader from "react-spinners/RingLoader";

    Loader = () => {
        const [loading, setLoading] = useState(false);

        const fechData = async () => {
            setLoading(true);
            await fetch("/").then(() => {
                https:login-app-api-ap19.onrender.com/signin
            })
        };

        useEffect(() => {
            setLoading(true)
            setTimeout(() => {
                setLoading(false)
          }, 8000)
        }, [])

        let [color] = useState("#0062ff");

        return  (
            <div className="loader">
                {loading ? (
                    <RingLoader
                    color={color}
                    loading={loading}
                    size={80}
                    />
                ) : "fetch data"}
            </div>
        );
      }      

export default Loader
